import{_ as a}from"./chunks/scratch.lGmWs6pP.js";import{j as i,c as o,b as l,ag as t}from"./chunks/framework.BlzMDYBM.js";const m=JSON.parse('{"title":"TuringThink 青少年编程","description":"","frontmatter":{},"headers":[],"relativePath":"kidsedu/6.lego.md","filePath":"kidsedu/6.lego.md","lastUpdated":1744879674000}'),r={name:"kidsedu/6.lego.md"};function d(c,e,n,h,s,p){return l(),o("div",null,e[0]||(e[0]=[t('<h1 id="turingthink-青少年编程" tabindex="-1">TuringThink 青少年编程 <a class="header-anchor" href="#turingthink-青少年编程" aria-label="Permalink to &quot;TuringThink 青少年编程&quot;">​</a></h1><h2 id="机构简介" tabindex="-1">机构简介 <a class="header-anchor" href="#机构简介" aria-label="Permalink to &quot;机构简介&quot;">​</a></h2><p><code>TuringThink</code> 是一家领先的少儿编程教育公司，致力于激发创造力，逻辑思维能力和多维度的学习能力，通过有趣而实践的课程，我们帮助学生掌握编程技能，并培养他们解决问题的能力。以 &quot; <code>TuringThink</code> &quot; 为商标，象征着思考、探索和创新。引导学生独立思考、探索世界，鼓励他们在软件编程的旅程中追求创新，我们的使命是培养下一代创造者和创新者，在数字时代充分发挥他们的潜力。</p><h3 id="教学系统" tabindex="-1">教学系统 <a class="header-anchor" href="#教学系统" aria-label="Permalink to &quot;教学系统&quot;">​</a></h3><p>自主研发乐特思教学系统 <code>LETS</code> - (Learning Explorer Teaching System)。该系统实时记录学生学习情况，并对学习数据进行大数据分析和数据挖掘。教师可以获得学生的多维度学习数据，从而因材施教，为每个学生定制最适合自己的学习方案.</p><h3 id="课程体系" tabindex="-1">课程体系 <a class="header-anchor" href="#课程体系" aria-label="Permalink to &quot;课程体系&quot;">​</a></h3><p><code>TuringThink</code> 提供多样化的课程，涵盖了多个编程语言和工具，旨在满足不同年龄和技能水平的学生需求。</p><ol><li><code>NOIP C++</code> :精心设计的 <code>NOIP C++</code> 课程，为学生提供深入学习 <code>C++</code> 编程语言的机会。此课程为学生参加全国奥林匹克信息学竞赛做准备。</li><li><code>Python</code> : <code>Python</code> 课程专注于培养学生对 <code>Python</code> 的理解和运用能力。通过实践项目和创意应用的开发，学生将在动手实践中提高编程技能。</li><li><code>Scratch</code> : 适合初学者的 <code>Scratch</code> 课程通过图形化编程界面，让学生创作动画、游戏和互动故事。这将激发学生的创造力和逻辑思维。</li><li><code>EV3</code> : 利用乐高机器人套件 <code>EV3</code> 进行机器人编程和控制，培养学生解决问题和团队合作的关键技能。</li><li><code>WeDo</code> : 针对年幼学生的乐高编程套件，通过有趣的项目探索基础编程概念，让学生在乐趣中启发好奇心和学习兴趣。</li><li><code>动力机械</code> : 乐高动力机械是一门以乐高积木为基础，结合电子和机械工程原理的创意课程。</li><li><code>大颗粒</code> : 针对年幼学生的乐高编程套件，通过有趣的项目探索基础编程概念，让学生在乐趣中启发好奇心和学习兴趣。</li></ol><h3 id="等级考试" tabindex="-1">等级考试 <a class="header-anchor" href="#等级考试" aria-label="Permalink to &quot;等级考试&quot;">​</a></h3><p>动力机械等级考试</p><h2 id="课程大纲" tabindex="-1">课程大纲 <a class="header-anchor" href="#课程大纲" aria-label="Permalink to &quot;课程大纲&quot;">​</a></h2><p><img src="'+a+'" alt=""></p><h3 id="课程安排" tabindex="-1">课程安排 <a class="header-anchor" href="#课程安排" aria-label="Permalink to &quot;课程安排&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>购买课程后与机构协定具体安排</p><p>授课对象: 5 - 8岁</p><p>课程名称: <code>动力机械</code></p><p>课程时长: 45分钟 每次2节课</p></div><h3 id="课程简介" tabindex="-1">课程简介 <a class="header-anchor" href="#课程简介" aria-label="Permalink to &quot;课程简介&quot;">​</a></h3><p><code>动力机械</code>本课程旨在通过使用乐高动力元件，结合基本的电子和机械工程原理，教授学生设计和构建各种乐高机械装置的能力。通过实际的项目和实验，学生将学到如何使用乐高积木创造运动、传动、感应等各种机械效果。</p><h4 id="课程内容" tabindex="-1">课程内容： <a class="header-anchor" href="#课程内容" aria-label="Permalink to &quot;课程内容：&quot;">​</a></h4><ol><li>乐高动力基础 <ol><li>介绍乐高动力元件，包括电机、传感器等。</li><li>学习如何连接和控制乐高动力元件。</li></ol></li><li>机械传动和运动 <ol><li>理解基本的机械结构，如齿轮、轴等。</li><li>学习不同的传动方式，包括直接传动、链传动等。</li></ol></li><li>传感器应用 <ol><li>掌握不同类型的传感器，如触碰传感器、颜色传感器等。</li><li>学习如何使用传感器实现自动化控制。</li></ol></li><li>创意机械设计 <ol><li>学生参与实际的乐高机械设计项目。</li><li>提倡创新思维，设计并构建独特的机械装置。</li></ol></li><li>团队项目 <ol><li>小组合作完成一个复杂的乐高机械项目。</li><li>强调团队协作和沟通技能。</li></ol></li></ol><h2 id="考试简介" tabindex="-1">考试简介 <a class="header-anchor" href="#考试简介" aria-label="Permalink to &quot;考试简介&quot;">​</a></h2><h2 id="联系方式" tabindex="-1">联系方式 <a class="header-anchor" href="#联系方式" aria-label="Permalink to &quot;联系方式&quot;">​</a></h2><p>客服电话:138-9727-1605</p><p>学校网址: <a href="https://turingthink.site" target="_blank" rel="noreferrer">https://turingthink.site</a></p>',22)]))}const b=i(r,[["render",d]]);export{m as __pageData,b as default};
